export function useClientCookieStore(cookieStore) {
    return {
        onRequestInit({ requestInit }) {
            requestInit.headers = {
                ...requestInit.headers,
                cookie: cookieStore.cookieString,
            };
        },
        onResponse({ response }) {
            const setCookie = response.headers.get('set-cookie');
            if (setCookie) {
                cookieStore.cookieString = setCookie;
            }
        },
    };
}
